mapper.insert:
INSERT 
  INTO BOARD
  		(
  		  BOARD_NO
  		, BOARD_TITLE
  		, BOARD_WRITER
  		, BOARD_CONTENT
  		, ORIGIN_NAME
  		, CHANGE_NAME
  		)
VALUES	(
		  SEQ_BNO.NEXTVAL
		, #{boardTitle}			
		, #{boardWriter}			
		, #{boardContent}			
		, #{originName}			
		, #{changeName}			
		)

mapper.update:
UPDATE BOARD 
   SET COUNT = COUNT + 1
 WHERE BOARD_NO = #{boardNo}

UPDATE
		BOARD
   SET  BOARD_TITLE = #{boardTitle}
   	  , BOARD_CONTENT = #{boardContent}
   	  , ORIGIN_NAME = #{originName}
   	  , CHANGE_NAME = #{changeName}
 WHERE BOARD_NO =#{boardNo}

mapper.insert:
INSERT 
	  INTO REPLY
