mapper.select:
SELECT COUNT(*) 
  FROM REVIEW_LIKE 
 WHERE REVIEW_NO = #{reNo}
   AND MEM_NO = #{memNo}

mapper.update:
UPDATE REVIEW
   SET REVIEW_LIKE = REVIEW_LIKE-1
 WHERE REVIEW_NO = #{reNo}

UPDATE REVIEW
   SET REVIEW_LIKE = REVIEW_LIKE+1
 WHERE REVIEW_NO = #{reNo}

mapper.delete:
DELETE FROM REVIEW_LIKE
 WHERE REVIEW_NO = #{reNo}
   AND MEM_NO = #{memNo}

mapper.select:
	SELECT 
			COUNT(*)
      FROM  REVIEW
     WHERE  CINFO_NO = #{cinfoNo}
       AND  MEM_NO = #{memNo}

mapper.update:
UPDATE REVIEW
   SET REVIEW_STATUS = 'N'
 WHERE REVIEW_NO = #{reNo}

